#include <QApplication>
#include <QInputDialog>
int main(int argc, char *argv[]) {
    QApplication app(argc, argv);
    bool ok_pressed;
    QString input_text = QInputDialog::getText(NULL, "Input Dialog", "Enter your text:", QLineEdit::Normal, QString(), &ok_pressed);
    if (ok_pressed) {
        qDebug() << "You entered: " << input_text;
    }
    return app.exec();
}

