    QApplication app(argc, argv);
    // Create a main window
    QMainWindow main_window;
    // Create a menu bar
    QMenuBar menu_bar = main_window.menuBar();
    // Create a menu
    QMenu *file_menu = menu_bar.addMenu("File");
    // Show the main window
    main_window.show();
    // Start the application
    return app.exec();
}

